<app-nav></app-nav>

<div class="container">
    <div class="row">
        <div class="col">
            <h2 class="text-center text-dark">View Doctor</h2>
            <div class="table-responsive">
                <table class="table custom-table">
                    <thead class="bg-primary text-white">
                        <tr>
                            <th>Doctor Id</th>
                            <th>Doctor Name</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr *ngFor="let p of serv.dList">
                            <td (click)="fillForm(p)">{{ p.DoctorId }}</td>
                            <td>{{ p.DoctorName }}</td>
                            <td>
                                <button class="btn btn-primary" [routerLink]="['/updateDoc', p.DoctorId]">Edit Doctor Details</button>
                                <button class="btn btn-danger" (click)="delDoctor(p.DoctorId)">
                                    <i class="fa fa-trash"></i> Delete
                                </button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</div>
