import { Component } from '@angular/core';
import { RestServiceService } from '../shared/rest-service.service';
import { ActivatedRoute } from '@angular/router';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-update-surgery',
  templateUrl: './update-surgery.component.html',
  styleUrls: ['./update-surgery.component.css']
})
export class UpdateSurgeryComponent {

  constructor(public objService:RestServiceService,private route: ActivatedRoute){}

  ngOnInit() {







   this.route.params.subscribe((params) => {



     const surgeryId = +params['id'];



     this.objService.getSurgeryById(surgeryId).subscribe((surgery) => {



       this.objService.SurgeryData = surgery; // Assuming this is how you set the form data



     });



   });



   this.resetForm();



 }



 resetForm(form?: NgForm) {



   if (form != null) {



     form.form.reset();



   }



 }



 onSubmit(form: NgForm) {



   this.updateRecord(form);



 }



 updateRecord(form:NgForm){



   this.objService.editSurgeryTime().subscribe(res=>{



     this.resetForm();



     this.objService.getTodaySurgery();



     alert('Surgery Details Updated!!!');



   },



   err=>{



     alert('Error!!!'+err);



   })



 }



 }


<app-nav></app-nav>

<div class="container">
    <div class="row m-5">
        <div class="col m-5">
            <h1 class="text-center text-white"><b>Edit Surgery Time</b></h1>
            <p class="text-dark-subtle text-center"><b>All fields are mandatory</b></p>
            <form #form="ngForm" autocomplete="off" (submit)="onSubmit(form)">
                <div class="input-group mb-3">
                    <div class="input-group-prepend">
                        <div class="input-group-text bg-warning-subtle">
                            <label><b>Surgery Id</b></label>
                        </div>
                    </div>
                    <input required name="SurgeryId" [(ngModel)]="objService.SurgeryData.SurgeryId" placeholder="Surgery Id" class="form-control" disabled />
                </div>

                <div class="input-group mb-3">
                    <div class="input-group-prepend">
                        <div class="input-group-text bg-warning-subtle">
                            <label><b>Doctor Id</b></label>
                        </div>
                    </div>
                    <input required name="DoctorId" [(ngModel)]="objService.SurgeryData.DoctorId" placeholder="Doctor Id" class="form-control" disabled />
                </div>

                <div class="input-group mb-3">
                    <div class="input-group-prepend">
                        <div class="input-group-text bg-warning-subtle">
                            <label><b>Surgery Date</b></label>
                        </div>
                    </div>
                    <input required name="SurgeryDate" [(ngModel)]="objService.SurgeryData.SurgeryDate" placeholder="Surgery Date" class="form-control" disabled />
                </div>

                <!-- Other form fields ... -->

                <div class="input-group mb-3">
                    <div class="input-group-prepend">
                        <div class="input-group-text bg-warning-subtle">
                            <label><b>Start Time (24 hours Format)</b></label>
                        </div>
                    </div>
                    <input required name="StartTime" [(ngModel)]="objService.SurgeryData.StartTime" placeholder="Start Time" class="form-control" />
                    <div *ngIf="form.controls.StartTime.invalid && (form.controls.StartTime.dirty || form.controls.StartTime.touched)" class="text-danger">
                        Start Time is required.
                    </div>
                </div>

                <div class="input-group mb-3">
                    <div class="input-group-prepend">
                        <div class="input-group-text bg-warning-subtle">
                            <label><b>End Time (24 hours Format)</b></label>
                        </div>
                    </div>
                    <input required name="EndTime" [(ngModel)]="objService.SurgeryData.EndTime" placeholder="End Time" class="form-control" />
                    <div *ngIf="form.controls.EndTime.invalid && (form.controls.EndTime.dirty || form.controls.EndTime.touched)" class="text-danger">
                        End Time is required.
                    </div>
                </div>

                <div class="input-group mb-3">
                    <div class="input-group-prepend">
                        <div class="input-group-text bg-warning-subtle">
                            <label><b>Surgery Category</b></label>
                        </div>
                    </div>
                    <input required name="SurgeryCategory" [(ngModel)]="objService.SurgeryData.SurgeryCategory" placeholder="Surgery Category" class="form-control" disabled />
                </div>

                <div class="form-group text-center">
                    <button type="submit" [disabled]="form.invalid">Update</button>
                    <a href='/viewtodayssurgery' class="ml-2">Cancel</a>
                </div>
            </form>
        </div>
    </div>
</div>
